/**
 * Main Component (functional)
 */
// libraries
import React, { Fragment, useRef } from "react";

// our stuff
import Header from "../components/Header.js";
import Content from "./Content.js";
import { Navbar,Nav,NavDropdown,Form,FormControl,Button } from 'react-bootstrap'
const Main = (props) => {
  // for a bug/problem in react/scheduler
  // combination we cannot use useState here
  const autoReschedule = useRef(false);

  return (
    <React.Fragment>
    <body>
    <header class="demo-header">
<div id="title-container">
<a id="title">
CrudManager demo
</a>
</div>
<div id="tools"><button class="b-tool keep b-widget b-button b-outer b-visible-scrollbar b-chrome b-icon-align-start" id="fullscreen-button" data-ref="fullscreenButton" data-btip="true"><i class="b-icon-fullscreen b-icon"></i><label data-owner-cmp="fullscreen-button"></label></button><button class="b-tool keep b-widget b-button b-outer b-visible-scrollbar b-chrome b-icon-align-start" id="b-button-1" data-ref="codeButton" data-btip="true"><i class="b-icon-code b-icon"></i><label data-owner-cmp="b-button-1"></label></button><button class="b-tool keep b-widget b-button b-outer b-visible-scrollbar b-chrome b-icon-align-start" id="b-button-2" data-ref="infoButton" data-btip="true"><i class="b-fa-cog b-fa"></i><label data-owner-cmp="b-button-2"></label></button></div>
</header>
     <Content autoReschedule={autoReschedule} />
    </body>
    </React.Fragment>
  );
};

export default Main;
